    //     }
    //     req.files.forEach((file) => {
    //       fs.unlinkSync(file.path);
    //     });
    //     fs.unlinkSync(outputPath);
    //   });
    // }
  } catch (err) {
    console.log(err);
  }
});

module.exports = router;

