  if (existingUser && await bcrypt.compare(password, existingUser.password)) {
    // кладём id нового пользователя в хранилище сессии (логиним пользователя)
    req.session.userId = existingUser.id;
    res.redirect('/Home');
  } else {
    const id = req.session.userId;
